mapper.select:
SELECT GAME_ROOM.ID, GAME_ROOM.ROOM_NAME, GAME_ROOM.GAME_NAME, GAME_ROOM.PLATFORM, GAME_ROOM.HOST, GAME_ROOM.IS_TIME_LIMITED, GAME_TYPE.TYPE_NAME_CHN FROM GAME_ROOM LEFT JOIN GAME ON GAME_ROOM.GAME_NAME = GAME.ID LEFT JOIN GAME_TYPE ON GAME.GAME_TYPE = GAME_TYPE.ID
AND GAME_ROOM.IS_TIME_LIMITED = true
AND GAME_ROOM.IS_TIME_LIMITED = false
OR GAME_TYPE.TYPE_NAME_CHN = #{roomSearchCondition.searchCondition1}
OR GAME_TYPE.TYPE_NAME_CHN = #{roomSearchCondition.searchCondition2}
OR GAME_TYPE.TYPE_NAME_CHN = #{roomSearchCondition.searchCondition3}
OR GAME_TYPE.TYPE_NAME_CHN = #{roomSearchCondition.searchCondition4}
OR GAME_TYPE.TYPE_NAME_CHN = #{roomSearchCondition.searchCondition5}
ORDER BY GAME_ROOM.ID, GAME_ROOM.GAME_NAME ASC
